<script>
  import { TimelineMax } from 'gsap';
  import { MorphSVGPlugin } from 'gsap/MorphSVGPlugin';

  // Register the plugin
  import { gsap } from 'gsap';
  gsap.registerPlugin(MorphSVGPlugin);

  export default {
    mounted() {
      //Click/Touch the guitar, or any key to play!

      //Convert circle elements to paths
      //MorphSVGPlugin.convertToPath("circle");

      //Note to self: Name stuff better in illustrator!
      //Varibles for the animation
      const stringStringyness = 2;
      const guitar = document.getElementById('guitar_2_');
      const stringStraight_6_ = document.getElementById('stringStraight_6_');
      const stringPlucked_6_ = document.getElementById('stringPlucked_6_');
      const stringStraight_7_ = document.getElementById('stringStraight_7_');
      const stringPlucked_7_ = document.getElementById('stringPlucked_7_');
      const stringStraight_8_ = document.getElementById('stringStraight_8_');
      const stringPlucked_8_ = document.getElementById('stringPlucked_8_');
      const stringStraight_9_ = document.getElementById('stringStraight_9_');
      const stringPlucked_9_ = document.getElementById('stringPlucked_9_');
      const stringStraight_10_ = document.getElementById('stringStraight_10_');
      const stringPlucked_10_ = document.getElementById('stringPlucked_10_');
      const stringStraight_11_ = document.getElementById('stringStraight_11_');
      const stringPlucked_11_ = document.getElementById('stringPlucked_11_');
      const speaker_1 = document.getElementById('speaker-1');
      const speakerWobble_1 = document.getElementById('speaker-wobble-1');
      const speaker_2 = document.getElementById('speaker-2');
      const speakerWobble_2 = document.getElementById('speaker-wobble-2');
      const note_1 = document.getElementById('note-1_2_');
      const note_2 = document.getElementById('note-2_2_');
      const note_3 = document.getElementById('note-3_2_');
      const note_4 = document.getElementById('note-4_2_');
      const spark_1 = document.getElementById('spark-1');
      const spark_2 = document.getElementById('spark-3');
      const spark_3 = document.getElementById('spark-4');

      //Declare timeline
      let pluck = new TimelineMax({
        paused: true
      });

      //String Animtaions
      pluck.to(stringStraight_6_, 0.1, {
        ease: "expo.out",
        morphSVG: {points: stringPlucked_6_.getAttribute('points')}
      })
        .to(stringStraight_6_, 1, {
          morphSVG: {points: stringStraight_6_.getAttribute('points')},
          ease: "elastic.out(2, 0.04)"
        })

        .to(stringStraight_7_, 0.1, {
          ease: "expo.out",
          morphSVG: {points: stringPlucked_7_.getAttribute('points')}
        }, "-=1.1")
        .to(stringStraight_7_, 1, {
          morphSVG: {points: stringStraight_7_.getAttribute('points')},
          ease: "elastic.out(2, 0.04)"
        }, "-=1")

        .to(stringStraight_8_, 0.1, {
          ease: "expo.out",
          morphSVG: {points: stringPlucked_8_.getAttribute('points')}
        }, "-=1.1")
        .to(stringStraight_8_, 1, {
          morphSVG: {points: stringStraight_8_.getAttribute('points')},
          ease: "elastic.out(2, 0.04)"
        }, "-=1")

        .to(stringStraight_9_, 0.1, {
          ease: "expo.out",
          morphSVG: {points: stringPlucked_9_.getAttribute('points')}
        }, "-=1.1")
        .to(stringStraight_9_, 1, {
          morphSVG: {points: stringStraight_9_.getAttribute('points')},
          ease: "elastic.out(2, 0.04)"
        }, "-=1")

        .to(stringStraight_10_, 0.1, {
          ease: "expo.out",
          morphSVG: {points: stringPlucked_10_.getAttribute('points')}
        }, "-=1.1")
        .to(stringStraight_10_, 1, {
          morphSVG: {points: stringStraight_10_.getAttribute('points')},
          ease: "elastic.out(2, 0.04)"
        }, "-=1")

        .to(stringStraight_11_, 0.1, {
          ease: "expo.out",
          morphSVG: {points: stringPlucked_11_.getAttribute('points')}
        }, "-=1.1")
        .to(stringStraight_11_, 1, {
          morphSVG: {points: stringStraight_11_.getAttribute('points')},
          ease: "elastic.out(2, 0.04)"
        }, "-=1")

        //Note Animations
        .to(note_1, 0.4, {x: "-50%", y: "-50%"}, "-=1.1")
        .to(note_1, 0.2, {opacity: 1, ease: "slow(0.7, 0.7, false)"}, "-=1.1")
        .to(note_1, 0.2, {opacity: 0, ease: "slow(0.7, 0.7, true)"}, "-=0.9")

        .to(note_2, 0.4, {x: "50%", y: "-50%"}, "-=1.1")
        .to(note_2, 0.2, {opacity: 1, ease: "slow(0.7, 0.7, false)"}, "-=1.1")
        .to(note_2, 0.2, {opacity: 0, ease: "slow(0.7, 0.7, true)"}, "-=0.9")

        .to(note_3, 0.4, {x: "-20%", y: "-50%"}, "-=1.1")
        .to(note_3, 0.2, {opacity: 1, ease: "slow(0.7, 0.7, false)"}, "-=1.1")
        .to(note_3, 0.2, {opacity: 0, ease: "slow(0.7, 0.7, true)"}, "-=0.9")

        .to(note_4, 0.4, {x: "50%", y: "-25%"}, "-=1.1")
        .to(note_4, 0.2, {opacity: 1, ease: "slow(0.7, 0.7, false)"}, "-=1.1")
        .to(note_4, 0.2, {opacity: 0, ease: "slow(0.7, 0.7, true)"}, "-=0.9")

        //Sparks Animations
        .to(spark_1, 0.4, {x: "-50%", y: "50%"}, "-=1.1")
        .to(spark_1, 0.2, {opacity: 1, ease: "slow(0.7, 0.7, false)"}, "-=1.1")
        .to(spark_1, 0.2, {opacity: 0, ease: "slow(0.7, 0.7, true)"}, "-=0.9")

        .to(spark_2, 0.4, {x: "-50%", y: "-50%"}, "-=1.1")
        .to(spark_2, 0.2, {opacity: 1, ease: "slow(0.7, 0.7, false)"}, "-=1.1")
        .to(spark_2, 0.2, {opacity: 0, ease: "slow(0.7, 0.7, true)"}, "-=0.9")

        .to(spark_3, 0.4, {x: "50%", y: "-50%"}, "-=1.1")
        .to(spark_3, 0.2, {opacity: 1, ease: "slow(0.7, 0.7, false)"}, "-=1.1")
        .to(spark_3, 0.2, {opacity: 0, ease: "slow(0.7, 0.7, true)"}, "-=0.9")

        //Speaker Animtaions
        .to(speaker_1, 0.1, {ease: "expo.out", morphSVG: speakerWobble_1}, "-=1.1")
        .to(speaker_1, 0.4, {morphSVG: speaker_1, ease: "elastic.out"}, "-=1")

        .to(speaker_2, 0.1, {ease: "expo.out", morphSVG: speakerWobble_2}, "-=1.05")
        .to(speaker_2, 0.4, {morphSVG: speaker_2, ease: "elastic.out"}, "-=0.95");


      //Trigger Audio
      var i = 0;

      function playAudio() {
        if (i < 12) {
          ++i;
        } else {
          i = 1;
        }
        var myAudio = document.getElementById("audio-" + i);
        myAudio.play();
      }

      function rockOut() {
        pluck.restart();
        pluck.play();
        playAudio();
      }

      //Do the stuff when clicked
      guitar.addEventListener("click", rockOut);
    }
  }
